function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
var relativo = (g_form.getValue('u_relativo'));
g_form.clearOptions('u_relativo');
   if (newValue == 'organizzazionev') {
  g_form.addOption('u_relativo', 'annullamento', 'Annullamento');
  g_form.addOption('u_relativo', 'rinvio', 'Rinvio');
g_form.addOption('u_relativo', 'evento2', 'Evento Aziendale');
	  g_form.addOption('u_relativo', 'orario', 'Orario');

    }
    if (newValue == 'proposta') {
	 g_form.addOption('u_relativo', 'rifiutata', 'Rifiutata');
	 g_form.addOption('u_relativo', 'accettata', 'Accettata');
       }
   if (newValue == 'organizzazione') {
		g_form.addOption('u_relativo', 'location', 'Location');
        g_form.addOption('u_relativo', 'pizzata', 'Pizzata');
        g_form.addOption('u_relativo', 'quota', 'Quota');
	    g_form.addOption('u_relativo', 'evento2', 'Evento informale');
	    g_form.addOption('u_relativo', 'annullamento', 'Annullamento');
	    g_form.addOption('u_relativo', 'orario', 'Orario');

    }





}
