sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var getData= new GlideAjax('GetDataAnnuncio');
	getData.addParam('sysparm_name','getData');
	getData.addParam('sysparm_number',g_form.getValue('idannuncio'));
	getData.getXML(populateDateField);

	function populateDateField (response){
		var nomeFromScriptInclude=response.responseXML.documentElement.getAttribute('answer');
		g_form.clearValue('datacommento');
		g_form.setValue('datacommento',nomeFromScriptInclude);
	}



}
