sys_script_include.script:
var BachecaUtils = Class.create();
BachecaUtils.prototype = {
    initialize: function() {
    },
isDateAfter : function(strDate1,strDate2){
	var testDate1=new GlideDateTime(strDate1);
		var testDate2=new GlideDateTime(strDate2);
	return (testDate1.after(strDate2));


},


	validazioneData : function(strDate1,giorni){
		var today= new GlideDateTime();
		var testDate1= new GlideDateTime(strDate1);
		today.addDaysLocalTime(giorni);
		return testDate1.before(today);

	},

    type: 'BachecaUtils'
};
