sys_script_include.script:
var GetDataAnnuncio = Class.create();
GetDataAnnuncio.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getData :function(){
		var dataRecord = new GlideRecord('x_570213_bacheca_annuncio');
		dataRecord.get(this.getParameter('sysparm_number'));
		if(dataRecord.u_tipo_annuncio=='organizzazionev'){
		return dataRecord.dataevento;
		} else {
		if(dataRecord.u_tipo_annuncio=='organizzazione')
		return dataRecord.datattivit;
	}


	},


    type: 'GetDataAnnuncio'
});
